declare(strict_types=1);

namespace Paustian\MelodyMixerModule\Entity\Repository;

use Paustian\MelodyMixerModule\Entity\Repository\Base\AbstractGraphicsAndSoundRepository;

/**
 * Repository class used to implement own convenience methods for performing certain DQL queries.
 *
 * This is the concrete repository class for graphics and sound entities.
 */
class GraphicsAndSoundRepository extends AbstractGraphicsAndSoundRepository
{
    // feel free to add your own methods here, like for example reusable DQL queries
    public function selectGraphicsAndSoundsFromLevel(int $level, int $exNum) : array {
        $qb = $this->_em->createQueryBuilder();
        $qb->select(array('tbl'))
            ->from($this->mainEntityClass, 'tbl')
            ->where($qb->expr()->eq('tbl.levelid', '?1'))
            ->andWhere($qb->expr()->eq('tbl.exNum', '?2'))
            ->setParameters([1 => $level, 2 => $exNum]);
        return $this->getQueryFromBuilder($qb)->getArrayResult();
    }
}
